<template>    <div>            {{count}}        <button @click="increment">+</button>        <button @click="decrement">-</button>        <button @click="get">get</button>        <slot>            {{msg}}        </slot>        <p>请求的值是：{{text}}</p>    </div></template><script>    module.exports = {        props:['msg'],        computed:{            count : function(){                return this.$store.getters.count            },            text : function(){                return this.$store.getters.text            }        },        methods : {            increment : function(){                this.$store.commit('increment');            },            decrement : function(){                this.$store.commit('decrement');            },            get : function(){                this.$http.get('/src/static/a.txt').then(function(res){                    this.$store.commit('getText',res.data);                },function(res){                })            }        }    }</script><style></style>