<template>    <div>        {{count}}        <button @click="increment">+</button>        <button @click="decrement">-</button>    </div></template><script>    module.exports = {        computed:{            count : function (){                return this.$store.getters.count            }        },        methods:{            increment : function(){                this.$store.commit('increment');            },            decrement : function(){                this.$store.commit('decrement');            }        }    }</script><style lang="scss">p{    font-size: 20px;    text-align: center;}</style>