<template>        <div>            <h1>汽车筛选</h1>            <div>                价格：                <span   class="price"                        v-for="price in typeCarList"                        :class="{active : currentPrice == price  }"                        @click="selectPrice(price)"                >            <!-- 用来循环分类 -->            {{price.label}}        </span>            </div>            <div>                <ul>                <li v-for="(car,index) in carListFilter">                <span>                    {{ index +" : "+ car.name + "(" + car.price + "万)" }}                </span>                </li>            </ul>            </div>        </div></template><script>module.exports = {    props : ['carLists'],    data : function(){        return {//                carLists : carLists,            typeCarList : [                {                    label : '小于5万',                    value : '<5'                },                {                    label : '5万-10万',                    value : '5-10'                },                {                    label : '10万-15万',                    value : '10-15'                },                {                    label : '15万-20万',                    value : '15-20'                }            ],            currentPrice : null //1、判断是否选中 2、获取到当前对象后做区分数据分类的操作        }    },    computed : {        carListFilter : function(){            var list = [];            if(!this.currentPrice){                list = this.carLists;            }else if(this.currentPrice.value === '<5'){                list = this.carLists.filter(function(car){                    return car.price < 5;                })            }else if(this.currentPrice.value === '5-10'){                list =   this.carLists.filter(function(car){                    return car.price <= 10 && car.price >= 5;                })            }else if(this.currentPrice.value === '10-15'){                list =   this.carLists.filter(function(car){                    return car.price <= 15 && car.price >= 10;                })            }else if(this.currentPrice.value === '15-20'){                list =   this.carLists.filter(function(car){                    return car.price >= 15;                })            }            return  list;        }    },    methods:{        selectPrice : function(price){            this.currentPrice = price;        }    }}</script> nbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbb;<style lang="scss">    .price{        border:1px solid #333;        padding: 3px 2px;        margin: 0px 2px;    }    .price.active{        border:1px solid red;        color:red;    }</style>